mapper.select:
SELECT l.LogID, l.OperationTime, l.OperatorID, u.Username as operatorName, u.FullName as operatorFullName, l.OperationType, l.ManuscriptID, l.Description FROM Logs l LEFT JOIN Users u ON l.OperatorID = u.UserID WHERE 1=1
AND l.OperationType = #{operationType}
AND l.OperationTime >= #{startDate}
AND l.OperationTime <= #{endDate}
ORDER BY l.OperationTime DESC
OFFSET (#{pageNum} - 1) * #{pageSize} ROWS FETCH NEXT #{pageSize} ROWS ONLY

SELECT COUNT(*)
FROM Logs l
LEFT JOIN Users u ON l.OperatorID = u.UserID
WHERE 1=1

    AND l.OperationType = #{operationType}


    AND l.OperationTime >= #{startDate}


    AND l.OperationTime <= #{endDate}
