INSERT INTO News (Title, Content, PublishDate, PublisherID, IsActive)
VALUES
    ('期刊投稿系统正式上线通知', '尊敬的作者、审稿人、读者：<br><br>欢迎使用本期刊投稿系统！系统现已正式上线运行，支持在线投稿、审稿、稿件管理等功能。请用户及时完善个人信息，熟悉系统操作流程。<br><br>如有任何问题，请通过系统反馈功能或联系编辑部邮箱：editor@journal.edu。<br><br>期刊编辑部<br>2024年1月1日', '2024-01-01 09:00:00', 1, 1),
    ('关于调整投稿格式要求的通知', '各位作者：<br><br>为了提高审稿效率，自2024年2月1日起，本期刊对投稿格式要求进行了调整。主要更新包括：<br>1. 增加ORCID号要求；<br>2. 参考文献格式采用最新版APA标准；<br>3. 图表分辨率要求提升至300dpi。<br><br>详细格式要求请下载最新版投稿模板。已提交稿件不受此影响。<br><br>期刊编辑部<br>2024-01-15 14:30:00', '2024-01-15 14:30:00', 2, 1),
    ('新增"人工智能与医疗健康"特刊征稿', '本期刊将于2024年7月出版"人工智能与医疗健康"特刊，现面向全球学者征稿。征稿范围包括但不限于：<br>- 医学影像智能分析<br>- 电子健康记录挖掘<br>- 药物发现与设计<br>- 精准医疗与个性化治疗<br><br>截稿日期：2024年4月30日<br>预计出版时间：2024年7月<br><br>投稿时请在系统中选择"特刊：人工智能与医疗健康"栏目。<br><br>特刊主编：陈强 教授', '2024-01-20 10:00:00', 2, 1),
    ('2023年度优秀审稿人表彰', '为感谢审稿专家的辛勤付出，编辑部评选出2023年度优秀审稿人，名单如下：<br><br>1. 周涛 教授（浙江大学）<br>2. 李娜 副教授（武汉大学）<br>3. 陈强 教授（北京大学）<br><br>优秀审稿人评选标准：<br>- 审稿数量与质量<br>- 审稿及时性<br>- 审稿意见的建设性<br><br>期刊编辑部<br>2024-01-25 16:20:00', '2024-01-25 16:20:00', 2, 1),
    ('系统维护通知（2月5日 22:00-24:00）', '尊敬的各位用户：<br><br>为提升系统性能，定于2024年2月5日（周一）22:00至24:00进行系统维护升级。维护期间，投稿系统将暂停服务。<br><br>请各位用户提前做好安排，如有紧急事务，请联系技术支持邮箱：support@journal.edu。<br><br>感谢您的理解与支持！<br><br>技术部<br>2024-02-01 11:00:00', '2024-02-01 11:00:00', 1, 1),
    ('关于使用AI写作工具的声明', '近期，编辑部注意到部分投稿中使用了AI写作工具。为确保学术诚信，特声明如下：<br><br>1. 作者应在论文中明确说明AI工具的使用情况；<br>2. AI生成的内容不应作为原创性研究成果；<br>3. 作者对论文所有内容承担全部责任；<br>4. 使用AI工具辅助写作但不披露，视为学术不端。<br><br>请各位作者严格遵守学术规范，共同维护良好的学术环境。<br><br>期刊编辑部<br>2024-02-10 15:45:00', '2024-02-10 15:45:00', 2, 1),
    ('新增"先进功能材料"专栏征稿启事', '为促进先进功能材料领域的发展，本刊新增"先进功能材料"专栏，现面向国内外专家学者征稿。征稿主题包括：<br>- 智能响应材料<br>- 能源存储与转换材料<br>- 生物医用材料<br>- 信息功能材料<br><br>本专栏由刘伟研究员担任栏目主编，欢迎投稿！<br><br>期刊编辑部<br>2024-02-15 09:30:00', '2024-02-15 09:30:00', 5, 1),
    ('2024年编委会会议圆满召开', '2024年2月20日，本期刊编委会会议在北京顺利召开。会议总结了2023年工作，讨论了2024年发展计划，重点议题包括：<br><br>1. 提高审稿效率和质量<br>2. 加强国际合作与交流<br>3. 推动开放获取出版<br>4. 提升期刊国际影响力<br><br>编辑部将根据会议决议，优化工作流程，为作者、审稿人提供更好的服务。<br><br>期刊编委会<br>2024-02-21 10:20:00', '2024-02-21 10:20:00', 2, 1),
    ('关于警惕假冒期刊网站的公告', '近期发现有不法分子仿冒本期刊网站，骗取作者投稿费和审稿费。特此提醒：<br><br>1. 本期刊官方网址：https://journal.edu<br>2. 本期刊不收取任何形式的审稿费；<br>3. 仅在稿件录用后收取版面费；<br>4. 缴费需通过官方支付渠道。<br><br>如有疑问，请及时联系编辑部核实。<br><br>期刊编辑部<br>2024-02-25 14:10:00', '2024-02-25 14:10:00', 3, 1),
    ('2023年度优秀论文奖评选结果公布', '经过严格评审，本刊2023年度优秀论文奖评选结果如下：<br><br>一等奖（1篇）：<br>"基于深度强化学习的机器人控制方法研究"，作者：张明等<br><br>二等奖（2篇）：<br>1. "新型钙钛矿太阳能电池效率提升研究"，作者：李华等<br>2. "多模态学习在医疗诊断中的应用"，作者：王芳等<br><br>三等奖（3篇）：略<br><br>颁奖仪式将于2024年3月15日在北京举行。<br><br>期刊编辑部<br>2024-02-28 16:00:00', '2024-02-28 16:00:00', 2, 1),
    ('清明节放假安排通知', '根据国家节假日安排，编辑部清明节放假安排如下：<br><br>放假时间：2024年4月4日（周四）至4月6日（周六），共3天。<br>上班时间：4月7日（周日）正常上班。<br><br>放假期间，投稿系统正常运行，但稿件处理可能会有延迟。紧急事务请联系值班编辑。<br><br>祝各位节日愉快！<br><br>期刊编辑部<br>2024-03-20 09:00:00', '2024-03-20 09:00:00', 3, 1),
    ('关于启用新版投稿系统的通知', '为提高用户体验，本期刊将于2024年4月1日启用新版投稿系统。新版系统主要改进：<br><br>1. 优化投稿流程，操作更便捷；<br>2. 增强移动端适配，支持手机投稿；<br>3. 新增稿件状态实时推送功能；<br>4. 改进审稿人匹配算法。<br><br>旧系统将于3月31日24:00停止服务，请用户及时完成相关操作。<br><br>技术部<br>2024-03-25 10:30:00', '2024-03-25 10:30:00', 1, 1),
    ('已过期新闻：2023年投稿指南（旧版）', '此为旧版投稿指南，已不再适用。请查看最新投稿指南。<br><br>投稿前请仔细阅读本指南，确保稿件格式符合要求。', '2023-12-15 14:00:00', 2, 0);
